
INSERT INTO [dbo].[ImporterFormat]
     ( [Id]
     , [Name] )
SELECT u.[Id]
     , u.[Name]
  FROM (
           SELECT 1 [Id], N'請求フリーインポーター'           [Name]
 UNION ALL SELECT 2 [Id], N'入金フリーインポーター'           [Name]
 UNION ALL SELECT 3 [Id], N'入金予定フリーインポーター'       [Name]
 UNION ALL SELECT 4 [Id], N'得意先マスターフリーインポーター' [Name]
       ) u
WHERE NOT EXISTS (
      SELECT 1
        FROM [dbo].[ImporterFormat] f
       WHERE f.[Id] = u.[Id] )
GO

